public class Node {    int value;    int height;    Node left;    Node right;    Node(int value) {        this.value = value;        this.height = 1;        this.left = null;        this.right = null;    }    @Override    public String toString() {        return "Node{" +                "value=" + value +                ", height=" + height +                ", left=" + left +                ", right=" + right +                '}';    }}